mapper.select:
		select cid,title,slug,created,modified,content,type,tags,categories,hits,commentsNum,allowComment,allowPing,allowFeed,status,author,date_format(gtmCreate,'%Y-%m-%d %T') gtmCreate,date_format(gtmModified,'%Y-%m-%d %T') gtmModified from blog_content
          
		  		   and cid = #{cid} 
		  		   and title = #{title} 
		  		   and slug = #{slug} 
		  		   and created = #{created} 
		  		   and modified = #{modified} 
		  		   and content = #{content} 
		  		   and type = #{type} 
		  		   and tags = #{tags} 
		  		   and categories = #{categories} 
		  		   and hits = #{hits} 
		  		   and commentsNum = #{commentsNum} 
		  		   and allowComment = #{allowComment} 
		  		   and allowPing = #{allowPing} 
		  		   and allowFeed = #{allowFeed} 
		  		   and status = #{status} 
		  		   and author = #{author} 
		  		   and gtmCreate = #{gtmCreate} 
		  		   and gtmModified = #{gtmModified} 
		  		
        
            
                order by ${sort} ${order}
            
			
                order by cid desc
			
        
		
			limit ${offset}, ${limit}

select count(*) from blog_content
   
  		   and cid = #{cid} 
  		   and title = #{title} 
  		   and slug = #{slug} 
  		   and created = #{created} 
  		   and modified = #{modified} 
  		   and content = #{content} 
  		   and type = #{type} 
  		   and tags = #{tags} 
  		   and categories = #{categories} 
  		   and hits = #{hits} 
  		   and commentsNum = #{commentsNum} 
  		   and allowComment = #{allowComment} 
  		   and allowPing = #{allowPing} 
  		   and allowFeed = #{allowFeed} 
  		   and status = #{status} 
  		   and author = #{author} 
  		   and gtmCreate = #{gtmCreate} 
  		   and gtmModified = #{gtmModified}

mapper.update:
update blog_content
title = #{title}, slug = #{slug}, created = #{created}, modified = #{modified}, content = #{content}, type = #{type}, tags = #{tags}, categories = #{categories}, hits = #{hits}, commentsNum = #{commentsNum}, allowComment = #{allowComment}, allowPing = #{allowPing}, allowFeed = #{allowFeed}, status = #{status}, author = #{author}, gtmCreate = #{gtmCreate}, gtmModified = #{gtmModified}
where cid = #{cid}
