mapper.select:
select
menuId,parentId,name,url,menuType,icon,orderNum,gmtCreate,gmtModified
from sys_menu

	 and menuId = #{menuId} 
	 and parentId = #{parentId} 
	 and name = #{name} 
	 and url = #{url} 
	 and menuType = #{type} 
	 and icon = #{icon} 
	 and orderNum = #{orderNum} 
	 and gmtCreate = #{gmtCreate} 
	 and gmtModified = #{gmtModified} 


	
		order by ${sort} ${order}
	
	
		order by menuId desc
	


		limit #{offset}, #{limit}

select count(*) from sys_menu

	 and menuId = #{menuId} 
	 and parentId = #{parentId} 
	 and name = #{name} 
	 and url = #{url} 
	 and menuType = #{type} 
	 and icon = #{icon} 
	 and orderNum = #{orderNum} 
	 and gmtCreate = #{gmtCreate} 
	 and gmtModified = #{gmtModified}

mapper.update:
update sys_menu
parentId = #{parentId}, name = #{name}, url = #{url}, menuType = #{type}, icon = #{icon}, orderNum = #{orderNum}, gmtCreate = #{gmtCreate}, gmtModified = #{gmtModified}
where menuId = #{menuId}
