mapper.select:
select
roleId,roleName,roleSign,remark,userIdCreate,gmtCreate,gmtModified
from sys_role

	 and roleId = #{roleId} 
	 and roleName = #{roleName} 
	 and roleSign = #{roleSign} 
	 and remark = #{remark} 
	 and userIdCreate = #{userIdCreate} 
	 and gmtCreate = #{gmtCreate} 
	 and gmtModified = #{gmtModified} 


	
		order by ${sort} ${order}
	
	
		order by roleId desc
	


	limit #{offset}, #{limit}

select count(*) from sys_role

	 and roleId = #{roleId} 
	 and roleName = #{roleName} 
	 and roleSign = #{roleSign} 
	 and remark = #{remark} 
	 and userIdCreate = #{userIdCreate} 
	 and gmtCreate = #{gmtCreate} 
	 and gmtModified = #{gmtModified}

mapper.update:
update sys_role
roleName = #{roleName}, roleSign = #{roleSign}, remark = #{remark}, userIdCreate = #{userIdCreate}, gmtCreate = #{gmtCreate}, gmtModified = #{gmtModified}
where roleId = #{roleId}
